`var all_durations = durations.durations || durations;
if (!Array.isArray(all_durations)) { all_durations = [ all_durations ]; };
var sum_durations = null;
var min_unit = 'year';
all_durations.forEach(
  function (duration_part) {
    var value = duration_part.duration.value || duration_part.value;
    var unit = duration_part.duration.unit || duration_part.unit;

    if (sum_durations == null)
    {
      sum_durations = moment.duration(value);
    }
    else
    {
      sum_durations = sum_durations.add(moment.duration(value));
    } 

    if      (min_unit == 'year')                    { min_unit = unit; }
    else if (min_unit == 'month' && unit == 'week') { min_unit = unit; }
    else if (min_unit == 'month' && unit == 'day')  { min_unit = unit; }
    else if (min_unit == 'week' && unit == 'day')   { min_unit = unit; }


  }
);
sum_durations;

var result = {
  duration:
  {
    value: sum_durations,
    unit: min_unit
  }
};
result;`
